/**
 * Stops an iterator when the callback returns a truthy value.
 */
export declare function stopWhen<T>(iterator: Iterator<T>, cb: (value: T, index: number) => boolean): Iterable<T>;
/**
 * Stops an iterator, once the count is reached.
 */
export declare function stopOnCount<T>(iterator: Iterator<T>, count: number): Iterable<T>;
/**
 * Stops an iterator, upon exceeding a maximum value.
 */
export declare function stopOnValue<T>(iterator: Iterator<T>, maxValue: T): Iterable<T>;
